mapper.select:
select CONVERT (AMOUNT-TENDER_AMOUNT , CHAR) syAmount ,CONVERT (CONVERT (TENDER_AMOUNT/AMOUNT*100, DECIMAL(6,2))
, CHAR) bfbAmount,
ID, NAME, AMOUNT, ANNUAL_RATE,
TENDER_START_TIME, TENDER_END_TIME, TENDER_INIT_AMOUNT, TENDER_INCREASE_AMOUNT,
TENDER_AMOUNT, TENDER_USERS,
STATUS,TIME_LIMIT,
HIGH_SINGLE_INVEST
from PRODUCT
where PUBLISH_STATUS='issue' OR PUBLISH_STATUS='notIssue'

    and NAME = #{name,jdbcType=VARCHAR}


    and CODE = #{code,jdbcType=VARCHAR}


    and GE_NAME = #{geName,jdbcType=VARCHAR}


    and GE_CODE = #{geCode,jdbcType=VARCHAR}


    and AMOUNT = #{amount,jdbcType=DECIMAL}


    and TIME_LIMIT = #{timeLimit,jdbcType=DECIMAL}


    and TIME_LIMIT_UNIT = #{timeLimitUnit,jdbcType=VARCHAR}


    and ANNUAL_RATE = #{annualRate,jdbcType=DECIMAL}


    and REPAY_TYPE = #{repayType,jdbcType=VARCHAR}


    and TENDER_START_TIME = #{tenderStartTime,jdbcType=VARCHAR}


    and TENDER_END_TIME = #{tenderEndTime,jdbcType=VARCHAR}


    and TENDER_INIT_AMOUNT = #{tenderInitAmount,jdbcType=DECIMAL}


    and TENDER_INCREASE_AMOUNT = #{tenderIncreaseAmount,jdbcType=DECIMAL}


    and Hx_NAME = #{hxName,jdbcType=VARCHAR}


    and Hx_Acct_No = #{hxAcctNo,jdbcType=VARCHAR}


    and AUDIT_STATUS = #{auditStatus,jdbcType=VARCHAR}


    and AUDIT_DESC = #{auditDesc,jdbcType=VARCHAR}


    and AUDIT_TIME = #{auditTime,jdbcType=VARCHAR}


    and AUDITOR_ID = #{auditorId,jdbcType=VARCHAR}


    and AUDITOR_NAME = #{auditorName,jdbcType=VARCHAR}


    and REMARK = #{remark,jdbcType=VARCHAR}


    and DATA_STATUS = #{dataStatus,jdbcType=CHAR}


    and CREATE_TIME = #{createTime,jdbcType=VARCHAR}


    and CREATOR_ID = #{creatorId,jdbcType=VARCHAR}


    and CREATOR_NAME = #{creatorName,jdbcType=VARCHAR}


    and EDIT_TIME = #{editTime,jdbcType=VARCHAR}


    and EDITOR_ID = #{editorId,jdbcType=VARCHAR}


    and EDITOR_NAME = #{editorName,jdbcType=VARCHAR}


    and TENDER_AMOUNT = #{tenderAmount,jdbcType=DECIMAL}


    and TENDER_USERS = #{tenderUsers,jdbcType=DECIMAL}


    and TENDER_AUDITOR_ID = #{tenderAuditorId,jdbcType=VARCHAR}


    and TENDER_AUDITOR_NAME = #{tenderAuditorName,jdbcType=VARCHAR}


    and TENDER_AUDIT_TIME = #{tenderAuditTime,jdbcType=VARCHAR}


    and REPAY_START_DATE = #{repayStartDate,jdbcType=VARCHAR}


    and REPAY_END_DATE = #{repayEndDate,jdbcType=VARCHAR}


    and PUBLISH_STATUS = #{publishStatus,jdbcType=VARCHAR}


    and PUBLISH_TIME = #{publishTime,jdbcType=VARCHAR}


    and PUBLISHER_ID = #{publisherId,jdbcType=VARCHAR}


    and PUBLISHER_NAME = #{publisherName,jdbcType=VARCHAR}


    and STATUS = #{status,jdbcType=VARCHAR}


    and TENDER_AUDIT_STATUS = #{tenderAuditStatus,jdbcType=VARCHAR}


    and PRODUCT_AGREEMENT_TYPE = #{productAgreementType,jdbcType=VARCHAR}


    and HIGH_SINGLE_INVEST = #{highSingleInvest,jdbcType=DECIMAL}


    and CONTRACT_NO = #{contractNo,jdbcType=VARCHAR}


    and TRUST_CONTRACT_NO = #{trustContractNo,jdbcType=VARCHAR}





    and PRO_ID = #{proId,jdbcType=VARCHAR}


    and IF_prepay = #{ifPrepay,jdbcType=VARCHAR}


    and Purchase_desc = #{purchaseDesc,jdbcType=LONGVARCHAR}


    and PRODUCT_ADVANTAGE = #{productAdvantage,jdbcType=LONGVARCHAR}


    and reasons_prepay = #{reasonsPrepay,jdbcType=LONGVARCHAR}


    and PROJECT_SUMMARY = #{projectSummary,jdbcType=LONGVARCHAR}

ORDER BY FIELD(STATUS, 'tender','init','success','repaying','repayed','prepayed','fail') asc ,
STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s') DESC

select

,

from PRODUCT


        and NAME = #{name,jdbcType=VARCHAR}


        and CODE = #{code,jdbcType=VARCHAR}


        and CONTRACT_NO = #{contractNo,jdbcType=VARCHAR}


        and AUDIT_STATUS = #{auditStatus,jdbcType=VARCHAR}


        and GE_NAME = #{geName,jdbcType=VARCHAR}


        and GE_CODE = #{geCode,jdbcType=VARCHAR}


order by STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s') desc

select

,

from PRODUCT
where DATA_STATUS = '1'

    and NAME = #{name,jdbcType=VARCHAR}


    and CODE = #{code,jdbcType=VARCHAR}


    and AUDIT_STATUS = #{auditStatus,jdbcType=VARCHAR}





    and STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s')>=#{startTime,jdbcType=CHAR}
    and STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s')<=#{endTime,jdbcType=CHAR}


    and (AUDIT_STATUS='init' or AUDIT_STATUS='fail')

order by STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s') desc

select

,

from PRODUCT
where DATA_STATUS ='1'
and AUDIT_STATUS = 'submit'

    and NAME = #{name,jdbcType=VARCHAR}


    and CODE = #{code,jdbcType=VARCHAR}


    and STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s')>=#{startTime,jdbcType=CHAR}
    and STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s')<=#{endTime,jdbcType=CHAR}

or AUDIT_STATUS = 'processing'
order by STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s') desc

select

,

from PRODUCT
where DATA_STATUS='1' and AUDIT_STATUS ='success'
and TENDER_AUDIT_STATUS='finish' and STATUS='init'

    and NAME = #{name,jdbcType=VARCHAR}


    and CODE = #{code,jdbcType=VARCHAR}


    and STATUS = #{status,jdbcType=VARCHAR}


    and PUBLISH_STATUS = #{publishStatus,jdbcType=VARCHAR}


    and STR_TO_DATE(REPAY_START_DATE, '%Y-%m-%d %H:%i:%s')>= #{repayStartDate,jdbcType=VARCHAR}


    and STR_TO_DATE(REPAY_END_DATE, '%Y-%m-%d %H:%i:%s')<= #{repayEndDate,jdbcType=VARCHAR}


    and STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s')>=#{startTime,jdbcType=CHAR}
    and STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s')<=#{endTime,jdbcType=CHAR}

order by STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s') desc

select

,

from PRODUCT
where DATA_STATUS='1' and AUDIT_STATUS ='success'
and TENDER_AUDIT_STATUS='finish' and STATUS != 'init'

    and NAME = #{name,jdbcType=VARCHAR}


    and CODE = #{code,jdbcType=VARCHAR}


    and STATUS = #{status,jdbcType=VARCHAR}


    and STR_TO_DATE(REPAY_START_DATE, '%Y-%m-%d %H:%i:%s')>= #{repayStartDate,jdbcType=VARCHAR}


    and STR_TO_DATE(REPAY_END_DATE, '%Y-%m-%d %H:%i:%s')<= #{repayEndDate,jdbcType=VARCHAR}


    and STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s')>=#{startTime,jdbcType=CHAR}
    and STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s')<=#{endTime,jdbcType=CHAR}

order by STR_TO_DATE(EDIT_TIME, '%Y-%m-%d %H:%i:%s') desc

mapper.update:
update PRODUCT
set  DATA_STATUS = #{dataStatus,jdbcType=CHAR}
where ID = #{id,jdbcType=VARCHAR}
